//ZNBK100.PR0038 Programa honek lehen 100 zenbakiak aterako ditu pantailatik
package eb1;

public class znbk100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//While erabiliz (Bitartean)
		int znbk=0;
		
		System.out.println("Hauek dira lehen 100 zenbakiak");
		
		//balioa jarri
		znbk=1;
		
		while (znbk<=100) {
			//Aginduak
			System.out.println(znbk);
			
			//eguneraketa
			znbk++;
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
